import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { User } from 'src/app/Modules/Model/Users';
import { AuthService } from 'src/app/Services/Auth/auth.service';

@Component({
  selector: 'navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'] 
})

export class NavbarComponent implements OnInit {
  User: User;
  Name: string;
  Bool: boolean;

  constructor(private AuthService: AuthService) { }

  ngOnInit(): void {
    this.AuthService.User().subscribe(
      response => {
        this.User = response;
        this.Name = this.User[0].name
      },
      (err: HttpErrorResponse) => {
        console.log(err);
      }
    )
    this.Bool = false;
  }

}
<nav class="nav">
    <div class="navbar d-flex-space">
        <div class="navbar-collapse">
            <h1> Welcome, {{ Name }} </h1>
            <div class="text">
                Discover whatever you need easily.
            </div>
        </div>
        <div class="nav-collapse d-flex">
            <div class="input-box d-flex" *ngIf="Bool">
                <div class="icon d-flex"> <i class="icon-search"></i> </div>
                <input type="text" placeholder="Search Product...">
            </div>
            <div class="filter" *ngIf="Bool"> 
                <button> 
                    <i class="icon-filter"></i>
                </button>
            </div>
        </div>
    </div>
</nav>
