import { Injectable, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ApiBasket } from 'src/app/Modules/Config/Api';
import { basket } from 'src/app/Modules/Model/basket';
import { Products } from 'src/app/Modules/Model/Products';
import { User } from 'src/app/Modules/Model/Users';

const httpOptions = {
  headers: new HttpHeaders({
    'Content-Type': 'application/json',
  })
}

@Injectable({
  providedIn: 'root'
})

export class BasketService implements OnInit {
  Basket: basket;
  dateOfToday: any;
  id: number = Math.floor(Math.random() * 100000);
  User: User

  constructor(private HttpClient: HttpClient, private AuthService: AuthSe) { 
    var date = new Date();
    this.dateOfToday = date.toISOString().split('T')[0];
  }

  ngOnInit(): void {
    
  }

  getBasket(): Observable<basket[]> {
    return this.HttpClient.get<basket[]>(ApiBasket , httpOptions);
  }

  setProductToBasket(Product: Products, User: User): Observable<basket> {
    this.Basket = {
      id: this.id,
      product: Product,
      date: this.dateOfToday,
      user: User,
      quantite: 1
    }
    return this.HttpClient.post<basket>(ApiBasket, this.Basket, httpOptions);
  }

}
