import { Component, OnInit } from '@angular/core';
import { Products } from 'src/app/Modules/Model/Products';
import { basket as Basket } from 'src/app/Modules/Model/basket';
import { BasketService } from 'src/app/Services/Basket/basket.service';

@Component({
  selector: 'panier',
  templateUrl: './panier.component.html',
  styleUrls: ['./panier.component.css']
})

export class PanierComponent implements OnInit  {
  Products: Products[];
  Baskets: Basket[];
  Total: number = 0;
  SubTotal: number = 0;
  DisCountSales: number = 50;
  TotalSalesTax: number = 20;

  constructor(private basketService: BasketService) {}

  ngOnInit(): void {
    this.basketService.getBasket().subscribe(
      (response) => {
        this.Baskets = response;
      }
    )
  }

  increase(Product: Basket): void {
    Product.quantite++;
    console.log(Product);
    this.basketService.UpdateItemInBaskets(Product).subscribe(
      (response) => console.log(response);
    )
    this.ngOnInit();
  }

  decrease(Product: Basket): void {

  }

  ngClearBasket(): void {

  }

  ngDeleteItemFromBasket(item: Basket): void {

  }
}
